import React, { useState, useEffect } from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Axios from "axios";
import NowPlaying from "./NowPlaying";
import { HomeWrapper } from "./style";

export default function Home() {
  const [nowPlaying, setnowPlaying] = useState([]);
  const [upComingMovies, setupComingMovies] = useState([]);

  useEffect(() => {
    getAllMovies();
  }, []);
  const getAllMovies = async () => {
    let apiKey = "d8bcf125fed775e8aa6239f8a8b1e3e6";
    const allMovies = await Axios.all([
      Axios.get(
        `https://api.themoviedb.org/3/movie/now_playing?api_key=${apiKey}&language=en-US&page=1`
      ),
      Axios.get(
        `https://api.themoviedb.org/3/movie/popular?api_key=${apiKey}&language=en-US&page=1`
      ),
    ]);
    setnowPlaying(allMovies[0].data.results);
    setupComingMovies(allMovies[1].data.results);
  };

  return (
    <div className="App">
      <input type="text" id="location" placeholder="Enter City Name" />
      <button id="searchLocation">Select</button>
      <HomeWrapper>
        <NowPlaying movie={nowPlaying} />
      </HomeWrapper>
    </div>
  );
}
