import React from 'react';
import {Link, Redirect} from "react-router-dom";
import {Button} from "react-bootstrap";
import './profile.css';

export default class Profile extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      	loginClicked: false,
	  	registerClicked: false,
	  	fields: {},
    	errors: {}
    }
  }

  loginButtonClicked = () => {this.setState({loginClicked: true});}

  registerButtonClicked = () => {this.setState({registerClicked:true});}

  handleValidation(){
	let fields = this.state.fields;
	let errors = {};
	let formIsValid = true;

	//Name
	if(!fields["name"]){
	   formIsValid = false;
	   errors["name"] = "Cannot be empty";
	}

	if(typeof fields["name"] !== "undefined"){
	   if(!fields["name"].match(/^[a-zA-Z]+$/)){
		  formIsValid = false;
		  errors["name"] = "Only letters";
	   }        
	}

	//Email
	if(!fields["email"]){
	   formIsValid = false;
	   errors["email"] = "Cannot be empty";
	}

	if(typeof fields["email"] !== "undefined"){
	   let lastAtPos = fields["email"].lastIndexOf('@');
	   let lastDotPos = fields["email"].lastIndexOf('.');

	   if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') === -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
		  formIsValid = false;
		  errors["email"] = "Email is not valid";
		}
   }  

   this.setState({errors: errors});
   return formIsValid;
}

  contactSubmit(e){
	e.preventDefault();

	if(this.handleValidation()){
	   alert("Form submitted");
	}else{
	   alert("Form has errors.")
	}

}

  handleChange(field, e){         
	let fields = this.state.fields;
	fields[field] = e.target.value;        
	this.setState({fields});
}

  render(){
    if(this.state.loginClicked){
      return <Redirect push to={"/"}/>;
    }
    return (
		<div className="container">
			<div className="login-container">
			<form name="contactform" className="login-form" onSubmit= {this.contactSubmit.bind(this)}>
				<h1>Login</h1>
				<br/>
				<div className="form-group">
					<fieldset>
						<input className="form-control" ref="name" type="email" id="email" placeholder="Email Address" onChange={this.handleChange.bind(this, "email")} value={this.state.fields["email"]}/>
						<span style={{color: "red"}}>{this.state.errors["email"]}</span>
						<br/>
						<br/>
						<input className="form-control" type="password" size="32" id="password" placeholder="Password"/>
						<br/>
						<br/>
						<Button variant="primary" type="submit" id="submit" value="submit" onClick={this.loginButtonClicked}>Login</Button>
						<br/>
						<br/>
						<label className="forgot-password"><Link to="/forgotpassword">Forgot Password?</Link></label>
					</fieldset>
				</div>
				</form>
			</div>

			<div className="register-container">    
				<form className="register-form">
				<h1>Register</h1>
				<div className="form-group">
					<input className="form-control" type="text" id="name" placeholder="Name"/>
					<br/>
					<br/>
					<input className="form-control" type="email" id="email" placeholder="Email Address"/>
					<br/>
					<br/>
					<input className="form-control" type="password" id="password" placeholder="Password"/>
					<br/>
					<br/>
					<input className="form-control" type="password" id="confirmPassword" placeholder="Confirm Password"/>
					<br/>
					<br/>
					<input type="checkbox" id="terms" name="terms" value="terms"/>
					<label for="terms"> I agree to terms and conditions.</label>
					<br/> 
					<br/>
					<Button variant="primary" type="submit" id="submit" value="submit" onClick={this.registerButtonClicked}>Register</Button>
					<br/>
				</div>
				</form>
			</div>
		</div>
     );
  }

}
