        super(props);
        console.dir(props);
        this.state={
            id:props.match.params.id,
            movieData:[],
        }
    }

    async componentDidMount() {
		let apiKey = "d8bcf125fed775e8aa6239f8a8b1e3e6";
		const allMovies = await Axios.all([
		  Axios.get(
			`https://api.themoviedb.org/3/movie/${this.state.id}?api_key=${apiKey}&language=en-US`
		  )
		]);
		this.setState({movieData:allMovies[0].data});
		console.log(allMovies[0].data.results);
  	}

