import React from 'react';
import Button from 'react-bootstrap/Button';

export default class ForgotPassword extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      submitClicked: false,
	  	fields: {},
    	errors: {}
    }
  }

    handlValidation(){
      let fields = this.state.fields;
      let errors = {};
      let formIsValid = true;

      if(!fields["email"]){
        formIsValid = false;
        errors["loginEmail"] = "Please Enter your Email address";
       }

       if(typeof fields["email"] !== "undefined"){
        let lastAtPos = fields["email"].lastIndexOf('@');
        let lastDotPos = fields["email"].lastIndexOf('.');

        if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') === -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
           formIsValid = false;
           errors["loginEmail"] = "Entered Email Address is not valid";
         }
      }  

      this.setState({errors: errors});
      return formIsValid;
    }

  submit(e){
    e.preventDefault();
    if(this.handleValidation()){
      this.setState({submitClicked: true});
    }

  }

  handleChange(field, e){         
    let fields = this.state.fields;
    fields[field] = e.target.value;        
    this.setState({fields});
  }

	render(){
		if(this.state.submitClicked){
			return <Redirect push to={"/profile"}/>;
		}
		return (
		<div className="container">
			<form className="form" name="contactform" onSubmit= {this.submit.bind(this)}>
			<div className="form-group">
				<fieldset>
				<h1>Enter Your Email Address</h1>
				<input className="form-control" type="email" id="email" name="email" onChange={this.handleChange.bind(this, "email")} value={this.state.fields["email"]}/>
				<span style={{color: "red"}}>{this.state.errors["email"]}</span>
				<br/>
				<br/>
				<Button type="submit" id="submit">Submit</Button>
				</fieldset>
				</div>
			</form>
		</div>
		);
	}
}
