import React, {Component} from 'react';
import {Modal, Button, Row, Col} from 'react-bootstrap';

class Genre extends Component{
    render(){
        return(
            <div>
                <label>Genre</label>
            </div>
        )
    }
}
class SortBy extends Component{
    render(){
        return(
            <div>
                <label>SortBy</label>
            </div>
        )
    }
}
class Language extends Component{
    render(){
        return(
            <div>
                <label>Language</label>
            </div>
        )
    }
}
class Mode2D3D4DX extends Component{
    render(){
        return(
            <div>
                <label>Mode2D3D4DX</label>
            </div>
        )
    }
}

export default class Filter extends Component{

    constructor(props){
        super(props);
        this.state = {
            showGenreSwitch : false,
            showLanguageSwitch : false,
            showSortBySwitch : false,
            show2D3D4DXSwitch : false,
        }
    }

    render(){


        return(
            <Modal
            {...this.props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
             >
            <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
            Filter
            </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <div className="container">
                <div>
                <Row>
                    <Col sm={6}>
                    <Row>
                    <Button 
                    variant="primary"
                    onClick={()=> this.setState({showGenreSwitch:true})}>Genre</Button>
                    </Row>
                    <br/>
                    <Row>
                    <Button 
                    variant="primary"
                    onClick={()=> this.setState({showSortBySwitch:true})}>SortBy</Button>
                    </Row>
                    <br/>
                    <Row>
                    <Button 
                    variant="primary"
                    onClick={()=> this.setState({showLanguageSwitch:true})}>Language</Button>
                    </Row>
                    <br/>
                    <Row>
                    <Button 
                    variant="primary"
                    onClick={()=> this.setState({show2D3D4DXSwitch:true})}>2D/3D/4DX</Button>
                    </Row>
                    </Col>
                    <Col sm={4}>
                        <div className="container">
                            {this.state.showGenreSwitch ? <Genre /> : null}
                            {this.state.showSortBySwitch ? <SortBy /> : null}
                            {this.state.showLanguageSwitch ? <Language /> : null}
                            {this.state.show2D3D4DXSwitch ? <Mode2D3D4DX /> : null}
                        </div>

                    </Col>
                </Row>
                </div>
            </div>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={this.props.onHide}>Apply</Button>
            </Modal.Footer>
            </Modal>
        )
    };
}
