import React, {Component} from 'react';
import {Modal, Button, Row, Col, Form} from 'react-bootstrap';

export default class Filter extends Component{

    constructor(props){
        super(props);
    }
    render(){
        return(
            <Modal
            {...this.props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
             >
            <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
            Filter
            </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <div className="container">
                <div>
                <Row>
                    <Col sm={6}>
                        <ul>
                            <li><label onClick={this.showGenre}>Genre</label></li>
                            <li><label onClick={this.showSortBy}>Sort By</label></li>
                            <li><label onClick={this.showLanguage}>Language</label></li>
                            <li><label onClick={this.show2D3D4DX}>2D/3D/4DX</label></li>
                        </ul>
                    </Col>
                </Row>

                </div>
            </div>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={this.props.onHide}>Apply</Button>
            </Modal.Footer>
            </Modal>
        )
    };
}
