import React from 'react';
import {Row, Col} from 'react-bootstrap';
import Axios from 'axios';

export default class Movie extends React.Component {

    constructor(props){
        super(props);
        console.dir(props);
        this.state={
            id:props.match.params.id,
            movieData:[],
        }
    }

    async componentDidMount() {
        let apiKey = "d8bcf125fed775e8aa6239f8a8b1e3e6";
        const movie = await Axios.get(`https://api.themoviedb.org/3/movie/${this.state.id}?api_key=${apiKey}&language=en-US`);
        this.setState({movieData:movie.data});
  	}

  render(){
    return (
        <div className="container">
            <Row>
                <Col>
                    <Row>
                    <img src={`https://image.tmdb.org/t/p/w500/${this.state.movieData.poster_path}`}/>
                    </Row>
                    <Row>
                        <label>{this.state.movieData.title}</label>
                    </Row>
                    <Row>
                        <label>{this.state.movieData.spoken_languages.name}</label>
                    </Row>
                </Col>
                <Col>

                </Col>
            </Row>
        </div>
        );
    }

    }
