import React, {Component} from 'react';
import {Modal, Button, Row, Col} from 'react-bootstrap';

class Genre extends Component{
    render(){
        return(
            <div>
                <label>Genre</label>
            </div>
        )
    }
}

export default class Filter extends Component{

    constructor(props){
        super(props);
        this.state = {
            showGenreSwitch : false,
            showLanguageSwitch : false,
            showSortBySwitch : false,
            show2D3D4DXSwitch : false,
        }
    }

    showGenre(){
        this.setState({showGenreSwitch : true});
    }

    render(){


        return(
            <Modal
            {...this.props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
             >
            <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
            Filter
            </Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <div className="container">
                <div>
                <Row>
                    <Col sm={6}>
                    <Row>
                        <label onClick={this.showGenre} color="red">Genre</label>
                    </Row>
                    <Row>
                        <label onClick={this.showSortBy}>Sort By</label>
                    </Row>
                    <Row>
                        <label onClick={this.showLanguage}>Language</label>
                    </Row>
                    <Row>
                        <label onClick={this.show2D3D4DX}>2D/3D/4DX</label>
                    </Row>
                    </Col>
                    <Col sm={4}>
                        <div className="container">
                            {this.state.showGenreSwitch ? <Genre /> : null}
                        </div>

                    </Col>
                </Row>
                </div>
            </div>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={this.props.onHide}>Apply</Button>
            </Modal.Footer>
            </Modal>
        )
    };
}
